SET NOCOUNT ON

-- Check that table does not already exist.
DECLARE @CustomFieldsExists INT
SELECT  @CustomFieldsExists = 1 

-- Check that table does not already exist.
IF NOT EXISTS (
            SELECT  *
            FROM    sysobjects tab 
            WHERE   (tab.id = object_id('[!ActiveSchema!].CUSTOMFIELDS'))
          )
BEGIN
  SELECT @CustomFieldsExists = 0
END

IF @CustomFieldsExists = 0
BEGIN
  CREATE TABLE [!ActiveSchema!].[CUSTOMFIELDS](
    [cfFieldID] [int] NOT NULL,
    [cfStopChar] [varchar](1) NOT NULL,
    [cfSupportsEnablement] [bit] NOT NULL,
    [cfEnabled] [bit] NOT NULL,
    [cfCaption] [varchar](30) NOT NULL,
    [cfDescription] [varchar](255) NOT NULL,
	[cfDisplayPIIOption] [bit] NOT NULL,
	[cfContainsPIIData] [bit] NOT NULL,
    [PositionId] [int] IDENTITY(1,1) NOT NULL
  ) ON [PRIMARY];
  -- Create primary index
  CREATE UNIQUE INDEX CUSTOMFIELDS_Index_Identity ON [!ActiveSchema!].CUSTOMFIELDS(PositionId);
  -- Create other indexes
  CREATE UNIQUE INDEX CUSTOMFIELDS_Index0 ON [!ActiveSchema!].CUSTOMFIELDS(cfFieldID, cfStopChar, PositionId);
END

IF @CustomFieldsExists = 0
BEGIN
  -- MH 12/12/2017 - Moved Inserts into EXEC as it was causing the script to crash when upgrading 2017-R1 or
  --                 earlier databases where the cfDisplayPIIOption and cfContainsPIIData don't exist - even
  --                 though this section wouldn't have been executed!
   
  -- Add records
  EXEC('
  ')
END

SET NOCOUNT OFF

